    <div class="card">
        <h1>Employee Login</h1>
        <form >
            <div class="form-group">
                <label>Email</label>
                <input type="email" [(ngModel)]="name" [ngModelOptions]="{standalone: true}" class="form-control" id="email" placeholder="Enter Email">
            </div>
            <div class="form-group">
                <label>Password</label>
                <input type="password" [(ngModel)]="password" [ngModelOptions]="{standalone: true}" class="form-control" id="password" placeholder="Enter Password">
            </div>

            <button type="submit" class="btn btn-primary" (click)="onSubmit()">Login</button>&nbsp;
